INSERT INTO `Cocktails` (`ID`, `Name`, `Description`, `Recipe`, `Taste`, `Alcohol`, `Image`, `CreatorID`,
                         `Valid`)
VALUES ('f94ae8d5-eed2-40c0-ac4e-5eb419c77bbf', 'Bloody Mary',
        'Une boisson épicée à base de vodka et de jus de tomate.',
        '- Dans un shaker, mélanger 4cl de vodka, 12 cl de jus de tomate, ½ cl de jus de citron , ½ cl de worcestershire, 2 gouttes de Tabasco.\r\n\r\n- Ajouter des glaçons puis filtrer et ajouter à convenance sel de céleri et poivre.\r\n\r\n- Servir dans un verre tumbler et décorer d’une branche de céleri.',
        'Strong', 5.00, 0, NULL, 1),
       ('27c2f5d8-4ba8-4017-89ef-a63ccbbd869c', 'Blue Lagoon', 'Un cocktail exotique et coloré grâce au curaçao bleu.',
        '- Remplissez le shaker à moitié de glaçons.\r\n- Versez la vodka, le curaçao bleu et le jus de citron.\r\n- Remuez énergiquement pendant une quinzaine de secondes.\r\n- Versez votre Blue Lagoon dans un verre long drink en retenant les glaçons avec une passoire à cocktails.',
        'Sweet', 4.00, 0, NULL, 1),
       ('a13f7789-acfb-40ed-8c51-2df323c43bfb', 'Caipirinha',
        'Le cocktail national du Brésil, à base de cachaça et de citron vert.',
        '- Découpez le citron en petits dés et placez-les dans un verre old fashioned.\r\n- Ecrasez à l’aide d’un pilon pour extraire le jus du citron vert.\r\n- Mettez des glaçons dans un torchon. puis Fermez-le et tapez énergiquement avec le bout d’un rouleau à pâtisserie afin d’obtenir des petits morceaux de glace.\r\n- Ajoutez la glace pilée dans le verre.\r\n- Versez le sirop de sucre et la cachaça.\r\n- Mélangez avec une cuillère à mélange et servez.',
        'Sour',  5.00, 0, NULL, 1),
       ('037b22c9-ad89-4e80-9c27-1d15a94f6b70', 'Cosmopolitan',
        'Un cocktail élégant à base de vodka et de jus de cranberry.',
        '- Remplissez votre shaker à moitié de glaçons.\r\n- Versez la vodka.\r\n- Ajoutez le triple sec et le jus de cranberry et le jus de citron vert.\r\n- Shakez le cocktail.\r\n- Prenez un verre et munissez-vous de la passoire à cocktail.\r\n- Filtrez le contenu dans votre verre à cocktail.\r\n- Posez une rondelle de citron sur le bord du verre.\r\n- Votre cocktail est prêt.',
        'Sour', 6.00, 0, NULL, 1),
       ('0b2b5896-2316-4e95-bfea-93ca5b1a5693', 'Daiquiri', 'Un cocktail rafraîchissant à base de rhum.',
        '- Munissez-vous d’un verre à cocktail et versez le rhum blanc, le jus de citron vert et le sirop de sucre en mélangeant légèrement à l’aide d’une cuillère à mélange.\r\n- Ajoutez 3 à 4 glaçons.\r\n- Décorez le rebord du verre avec une rondelle de citron vert.\r\n- Dégustez',
        'Sour', 5.00, 0, NULL, 1),
       ('cecf2ca7-a1c8-4de7-9df6-51cef11f9055', 'Gin Tonic', 'Un mélange simple et rafraîchissant.',
        '- Ajoutez des glaçons dans votre verre piscine.\r\n- Versez le gin.\r\n- Pour un mélange idéal du tonic et du gin, penchez délicatement votre verre piscine avant d’allonger votre cocktail avec le tonic.\r\n- Parfumez votre Gin Tonic avec le zeste d’un citron jaune bien frais.\r\n- Mélangez-le délicatement à l’aide d’une cuillère à cocktail pour libérer tous les arômes de votre Gin Tonic.\r\n- Votre cocktail est prêt',
        'Strong', 5.00, 0, NULL, 1),
       ('201d087e-4b81-4155-9270-9a5352fe39a7', 'Mai Tai', 'Un cocktail tropical complexe et équilibré.',
        '- Ajoutez des glaçons dans le shaker.\r\n- Versez le Rhum Blanc, le Rhum, le triple sec, le jus de citron vert pressé et le sirop d’orgeat.\r\n- Pour finir, secouez le tout énergiquement pour bien mélanger l’ensemble.\r\n- Versez votre Mai Tai dans un verre long drink.',
        'Sweet', 6.00, 0, NULL, 1),
       ('3ac3f88d-d2db-4612-acc7-1060aefcc8bc', 'Margarita',
        'Un cocktail acidulé et salé, parfait pour les amateurs de tequila.',
        '- Mettez votre verre à cocktail dans votre réfrigérateur ou congélateur le temps de la préparation.\r\n- Dans un shaker, ajoutez des glaçons.\r\n- Versez la tequila, le triple sec et le jus de citron vert.\r\n- Secouez énergiquement pendant quelques secondes.\r\n- Récupérez votre verre et versez le mélange à l’aide d’une passoire à cocktail afin de filtrer les glaçons.',
        'Sour', 6.00, 0, NULL, 1),
       ('3be84c65-115f-49d1-b62c-730d785cfaf4', 'Mojito',
        'Un cocktail rafraîchissant à base de menthe et de citron vert.',
        '- Dans un verre à Mojito, disposez 6 à 8 feuilles de menthe entières préalablement lavées et essuyées.\r\n- Prenez votre demi citron vert. Coupez-le en dés. Déposez-le dans le verre.\r\n- À l’aide d’un verre doseur, versez 2 cl de sirop de canne dans votre verre à mojito.\r\n- Écrasez délicatement avec un pilon les dés de citron vert, les feuilles de menthe et le sirop de canne afin d’en extraire le jus du citron et le parfum des feuilles de menthe.\r\n- À l’aide d’un verre doseur, versez 4 cl de rhum blanc dans le verre.\r\n- Préparez la glace pilée en déposant les glaçons dans un torchon, refermez-le. À l’aide d’un pilon spécial cocktail, frappez fortement pour casser la glace.\r\n- Ajoutez votre glace pilée dans le verre.\r\n- Allongez votre cocktail avec de l’eau gazeuse.\r\n- Mélangez le tout à l’aide d’une cuillère ou un touilleur à mojito avant dégustation.',
        'Sweet', 5.00, 0, NULL, 1),
       ('fe5f0820-5787-4d8e-a357-2a54187bce3b', 'Old Fashioned', 'Une boisson classique au goût riche et complexe.',
        '- Au fond d’un verre Old Fashioned, posez le morceau de sucre préalablement imbibé d’un trait de bitter Angostura.\r\n- Ajoutez un trait d’eau gazeuse et écrasez le sucre jusqu’à sa dissolution complète.\r\n- Ajoutez des glaçons et versez le whisky .\r\n- Remuez avec une cuillère à mélange pendant une quinzaine de secondes.\r\n- Coupez un zeste d’orange puis nettoyez les bords.\r\n- Exprimez le zeste au-dessus du verre.\r\n- Ajoutez le zeste d’orange dans votre cocktail.',
        'Strong', 6.00, 0, NULL, 1),
       ('07fa1eec-4efc-4281-bf13-185c095f31c0', 'Piña Colada',
        'Une boisson douce et exotique à base d ananas et de noix de coco.',
        '- Dans un shaker, versez le rhum blanc, le jus d’ananas, la crème de coco et le sirop de canne.\r\n- Secouez énergiquement pendant 1 minute afin de bien les mélanger.\r\n- Munissez-vous de votre verre long drink.\r\n- Rafraîchissez le verre en y ajoutant une bonne quantité de glaçons.\r\n- Versez-y le mélange du shaker.\r\n- Dégustez votre Piña Colada très fraîche.',
        'Sweet', 4.00, 0, NULL, 1),
       ('e8cff121-08bc-4e45-81d1-0884000e1983', 'Sex on the Beach',
        'Un cocktail fruité et populaire dans les bars estivaux.', '- Remplissez votre shaker de glaçons.\r\n- Versez la vodka,
        le Schnapps pêche ou la crème de pêche et le jus de cranberry.\r\n- Shakez votre mélange pendant une dizaine de secondes.\r\n- Remplissez votre verre long drink de glaçons.\r\n- Versez le jus d’orange.\r\n- Ajoutez-y votre mixture préalablement mélangée en la filtrant avec une passoire à cocktail.',
        'Sweet', 4.00, 0, NULL, 1),
       ('0a061592-6037-43ff-83e3-5c94d256940d', 'Tequila Sunrise',
        'Un cocktail visuellement impressionnant avec des couches de couleurs rappelant un lever de soleil.',
        '- Mettez des glaçons dans un verre long drink.\r\n- Versez dans l’ordre la Tequila puis le jus d’orange.\r\n- Remuez doucement à l’aide d’une cuillère à mélange.\r\n- Versez lentement le sirop de grenadine en vérifiant que le sirop tombe bien au fond du verre.\r\n- Donnez un petit coup de cuillère à cocktail pour créer un joli dégradé typique du Tequila Sunrise.',
        'Sweet', 55.00, 0, NULL, 1),
       ('ac4eba8a-24ef-4b28-839e-f92e03625202', 'Whisky Sour', 'Une boisson classique au goût acidulé.', '- Dans un shaker rempli de glaçons,
        pressez le jus de citron vert.\r\n- Ajoutez ensuite le whisky et le sirop de canne et un blanc d’œuf.\r\n- Remuez énergiquement le shaker pendant une dizaine de secondes.\r\n- Versez dans un verre à cocktail type cobbler en retenant les glaçons à l’aide d’une passoire à cocktail.\r\n- Dégustez votre cocktail Whisky Sour !',
        'Sour', 5.00, 0, NULL, 1),
       ('2de91c37-668e-4bdd-bf3f-074ca6f12c73', 'White Russian', 'Une boisson riche et crémeuse à base de vodka,
        de liqueur de café et de crème.', '- Munissez-vous d’un verre old fashioned et déposez-y quelques glaçons.\r\n- Rajoutez-y la vodka et la liqueur de café.\r\n- Prenez la cuillère à mélange et placez-la au milieu du verre,
        en la tenant droite. Faites couler lentement la crème fraîche liquide sur la cuillère, dans le verre,
        afin de créer une couche de crème distincte.\r\n- Mélangez le tout lentement,
        afin que la crème reste surtout en surface.\r\n- Votre cocktail est prêt.', 'Sweet', 5.00, 0, NULL, 1);

